#This is the main filefrom cowproblem.cowfunctions import *import seaborn as snsimport matplotlib as mpimport numpy as np##############################################################################g=9.81 #gravitaional constantk=.1 #drag constantm=1000 # mass in kgpos0=[0, 15] #initial posistion in metersv0=[3,4] #initial position in m/sF0=[0,m*g] # initial force in Nt0=0  #initial time in secondstf= 60 #final time in secondsSteps = 6000    #steps defines the resolution dt=np.ceil((tf - t0)/Steps) #this is the small change in timet=np.arange(t0,tf,dt) #creates a timeseries from t0 to tf with size Steps##############################################################################xlist=[pos0]vlist=[v0]Flist=[F0]for i  in range(len(list(t))-1):    xlist.append(Newpos(xlist[i],vlist[i],dt))    vlist.append(Newv(m,vlist[i],Flist[i],dt))    Flist.append(Forces(m,vlist[i],k))    ##############################################################################